/**
 * Specifies the Chip type
 *
 * The possible values are:
 * - `none` (default)
 * - `success`
 * - `error`
 * - `warning`
 * - `info`
 *
 * @example
 * ```ts-no-run
 * <kendo-chip [type]="'success'"></kendo-chip>
 * ```
 *
 */
export declare type ChipType = 'none' | 'success' | 'error' | 'warning' | 'info';
