/**
 * Specifies what view the ColorPicker will render in the popup.
 *
 * The possible values are:
 * * (Default) `gradient`
 * * `palette`
 * * `combo`
 */
export declare type ColorPickerView = 'gradient' | 'palette' | 'combo';
